import { Button, AppBar, Card, CardContent, CardHeader, TextField, CircularProgress, Container, Grid, IconButton, makeStyles, Toolbar, Typography, Box } from "@material-ui/core";

import { Alert, AlertTitle } from "@material-ui/lab";
import { useSnackbar } from "notistack";
import { getUserInfo } from "../../services/user.service";
import { Person, Menu as MenuIcon } from "@material-ui/icons";
import authService from "../../services/auth.service";
import { useNavigate } from "react-router-dom";
import {axios} from "axios";
import { useState } from 'react';
import logo from '../../images/logo.png';
import CurrencyInput from 'react-currency-input-field';

const useStyles = makeStyles((theme) => ({
    root: {
        height: "100%"
    },
    grid: {
        height: "100%"
    },
    logoutBtn: {
        marginLeft: "auto"
    }
}));



export default function loanCalculator(props) {



    const headuser = localStorage.getItem("user");
    const headname = localStorage.getItem("name");
    const headtoken = localStorage.getItem("token");

    console.log(headuser);
    console.log(headname);
    console.log(headtoken);

    const msgTwo = "Welcome, " + headname + "!";

    const [input, setInput] = useState();

        const handleLogin = (e) => {
            e.preventDefault();
            //updateBalance(input);
            window.location.href = "http://localhost:5173/loanCalculator";
        };

    //update Balance
        function updateBalance(amt){

            const name = window.localStorage.getItem("name");

            var requestOptions = {
              method: 'POST'
            };

            fetch("http://localhost:8080/updateBalance/" + name + "/" + amt, requestOptions)
              .then(response => response.text())
              .then(result => console.log(result))
              .catch(error => console.log('error', error));
        }

        handleReset = ({ fields, form }) => {
                this.setState({ password: '' }) // manually reset controlled fields ("password")
            }






return (
    <div>
        <Container maxWidth={false} className="App-header-two">
            <ul className="stacked-window">
                <li >
                    <Container className="content-window-one">
                        Commerce Bank App
                    </Container>
                    <Container maxWidth={"sm"} id="calculatorform">
                                    <Card id="formTwo" >
                                        <CardHeader title={"Account Balance"}/>
                                        <CardHeader title={"TITLE"} className="msg"/>
                                        <CardContent >
                                            <form onSubmit={handleLogin} onReset={this.handleReset}>>

                                                        <TextField
                                                            variant="outlined"
                                                            placeholder={"Starting Account Balance"}
                                                            fullWidth
                                                            className="field"
                                                            decimalsLimit={2}
                                                            onValueChange={(value, name) => console.log(value, name)}
                                                            prefix="$"
                                                            value={input ?? ""}
                                                            onChange={e => setInput(e.target.value)}
                                                        />
                                                       <Button type="submit" id="loginButtonTwo"> Submit</Button>
                                                       <Button type="reset" value="Reset" id="loginButtonTwo"> Clear</Button>
                                            </form>
                                        </CardContent>
                                    </Card>
                    </Container>
                </li>
                <li >
                    <Container className="content-window-two">
                        {msgTwo}
                    </Container>
                </li>
            </ul>

        </Container>

    </div>
);

}
