import { Button, AppBar, Card, CardContent, CircularProgress, Container, Grid, IconButton, makeStyles, Toolbar, Typography, Box } from "@material-ui/core";
import { CardHeader, TextField } from "@material-ui/core";
import { Alert, AlertTitle } from "@material-ui/lab";
import { useSnackbar } from "notistack";
import { useEffect } from "react";
import { getUserInfo } from "../../services/user.service";
import { Person, Menu as MenuIcon } from "@material-ui/icons";
import authService from "../../services/auth.service";
import { useNavigate } from "react-router-dom";
import {axios} from "axios";
import { useState } from "react";
import { login, setToken } from "../../services/auth.service";
import logo from '../../images/logo.png'
import CurrencyInput from 'react-currency-input-field';

const useStyles = makeStyles((theme) => ({
    root: {
        height: "100%"
    },
    grid: {
        height: "100%"
    },
    logoutBtn: {
        marginLeft: "auto"
    }
}));



export default function HomeView(props) {

    const history = useNavigate();
    const headname = localStorage.getItem("name");

    const UI_URL = "http://localhost:5173/";
    const BE_URL = "http://localhost:8080/";

    var raw = "";

    var requestOptions = {
      method: 'GET',
      body: raw,
      redirect: 'follow'
    };

    var obj;

    fetch("http://localhost:8080/getAmountById/1", requestOptions)
      .then(response => response.json())
      .then(result => {
        obj = result;
        console.log(obj);
      })
      .catch(error => console.log('error', error));

      async function fetchExam(id) {
              try {
                  const response = await fetch(`http://localhost:8080/getAmountById/${id}`, {
                      method: 'GET',
                      body: raw,
                      credentials: 'same-origin'
                  });
                  const exam = await response.json();
                  return exam;
              } catch (error) {
                  console.error(error);
              }
          }




    const msg = "Welcome to Commerce Bank, " + headname + "! Please enter your starting Account Balance." + obj;

    const handleLogin = (e) => {
        e.preventDefault();
        window.location.href = UI_URL + "calanderView";
    };



    return (
        <Container maxWidth={false} className="App-header">
            <Container maxWidth={"sm"}>
                <Card id="form">
                    <CardHeader title={"Account Balance"}/>
                    <CardHeader title={msg} className="msg"/>
                    <CardContent >
                        <form onSubmit={handleLogin} >

                                    <CurrencyInput
                                        variant="outlined"
                                        placeholder={"Starting Account Balance"}
                                        fullWidth
                                        className="field"
                                        decimalsLimit={2}
                                        onValueChange={(value, name) => console.log(value, name)}
                                        prefix="$"
                                    />
                                   <Button type="submit" id="loginButton"> Enter</Button>
                        </form>
                    </CardContent>
                </Card>
            </Container>
            <div id="logo">
                <img src={logo} width={400} alt="" />
             </div>
        </Container>


    );
}
