//first tab

import {React, useState} from "react";
import {DAYS, MONTHS} from './calendarComponents/conts';
import { CalendarHead, ClenderBody, HeadDay, SevenColGrid, StyledDay, Wrapper } from "./calendarComponents/styled";
import { IconButton } from "@material-ui/core";
import ArrowBack from '@material-ui/icons/ArrowBack';
import ArrowForward from '@material-ui/icons/ArrowForward';
import { range, getDaysInMonth, getSortedDays } from "./calendarComponents/utils";




const FirstTab = ({startingDate}) => {

  const [currentMonth, setCurrentMonth] = useState(startingDate.getMonth());
  const [currentYear, setCurrentYear] = useState(startingDate.getFullYear());
  const DAYSINAMONTH = getDaysInMonth(currentMonth, currentYear);


  return (
    <div className="FirstTab">
      <p>First Tab</p>
      {/* First tab content will go here */}
      <Wrapper>
        <CalendarHead>
            <IconButton> {/*onClick={sayHi}*/}<ArrowBack /></IconButton>
            {MONTHS[currentMonth]} {currentYear}
            <IconButton> {/*onClick={sayHi}*/}<ArrowForward /></IconButton>
        </CalendarHead>
        <SevenColGrid>
            {getSortedDays(currentMonth, currentYear).map((day) => (
                <HeadDay>{day}</HeadDay>
            ))}
        </SevenColGrid>
        <ClenderBody fourCol={DAYSINAMONTH === 28}>
            {range(DAYSINAMONTH).map((day) => (<StyledDay active={
                areDatesTheSame(new Date(), getDateObj(day, currentMonth, currentYear))
            }>{day}</StyledDay>))}
        </ClenderBody>
      </Wrapper>
    </div>
  );
};
export default FirstTab;
